INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('4', '0', '1', '', '17', '2012年我省教育重点抓好十个方面实事', '201300001', '曹英山', '高考预科部', '', '<p>&nbsp; <span style="font-family: Simsun; font-size: 13px; line-height: 25px; ">记者从2012年度全省教育局长会议获悉，2012年我省教育系统将重点抓好十个方面的实事，以进一步推进教育科学和谐发展，使群众更直接地感受到浙江教育的实实在在进步。</span><br style="font-family: Simsun; font-size: 13px; line-height: 25px; " />
<span style="font-family: Simsun; font-size: 13px; line-height: 25px; ">　　这十个方面的实事分别是：一是竣工校舍面积100万平方米，全面完成校舍安全工程三年建设任务；二是竣工乡镇中心幼儿园100所，乡镇等级中心幼儿园覆盖率达到90%；三是把农村中小学低收入家庭子女爱心营养餐标准由350元提高到750元；四是省级完成培训中小学校长和幼儿园园长3000名，学科骨干教师800名，特殊教育骨干教师400名，选派100名高校优秀中青年骨干教师公派出国研修、50名中小学名师名校长赴海外培训；五是为中小学新装备班级多媒体8000套，为欠发达地区农村中小学新装备计算机教室100个，为中小学新建设网络课程100门；六是为义务教育阶段农村和欠发达地区城镇中小学包括民工子弟学校配备纸质图书180万册，为每个中小学生开设&ldquo;数字书房&rdquo;；七是建立覆盖全省的国家教育考试网络指挥平台体系，完成300个国家教育考试标准化考点和11000个标准化考场建设；八是扶持建设外来务工人员子女学校项目50个；九是资助建设特殊教育学校项目18个；十是完成下沙高教园区青年教室宿舍一期工程主体施工，竣工建筑面积7.78万平方米。</span></p>', '', '\HrSchoolFiles\1\UpFiles\网站内容\1\4.jpg', '', '', '1', '2', '0', '', '', '', ' ', '0', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('4', '0', '2', '', '2', '	电气专业组教师赴第一批国家改革示范校取经，拓宽数字化资源库建设思路', '201210001', '王吉', '机电部电子电工组', '', '<div class="Section0" style="layout-grid:  15.6pt none">
<p class="p0" style="margin-top: 0pt; text-indent: 24.78pt; margin-bottom: 0pt"><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes">5月24-25号两天，我校电子电工专业数字化资源库建设团队，在谢华林主任的带领下，赴绍兴县职教中心、鄞州职教中心学习、取经。</span><span style="font-family: 宋体; font-size: 10.5pt; font-weight: bold; mso-spacerun: yes"><o:p></o:p></span></p>
<p class="p0" style="margin-top: 0pt; margin-bottom: 0pt"><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes">&nbsp;&nbsp;&nbsp;</span><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes">绍兴县职教中心机电技术部魏云飞主任携电子、机械两大专业负责人热情接待了我们。在会上，双方就如何开展数字化资源库建设问题进行了探讨。机电技术部魏云飞主任对他们学校如何开展电气专业、数控专业数字化资源库建设进行业详细介绍，他们以共建共享的方式参与由上海大众工业学校创立的数字化资源库，为学校专业课教师提供了丰富的上课资源。</span><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes"><o:p></o:p></span></p>
<p class="p0" style="margin-top: 0pt; text-indent: 21pt; margin-bottom: 0pt"><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes">结束了对绍兴县职教中心的参观访问之后，我们又赶往宁波鄞州职教中心学习、考察。在鄞州职教中心，校务办陈主任和电子电工专业负责人方爱平老师热情接待了我们。校办主任详细介绍了他们学校创建国家级示范校的建设过程及取得的成果。电子电工专业负责人方爱平老师向我们介绍了他们是如何以共建共享的形式参与南京工程学校负责的电气专业数字化资源库建设。</span><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes"><o:p></o:p></span></p>
<p class="p0" style="margin-top: 0pt; margin-bottom: 0pt"><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes">&nbsp;&nbsp;&nbsp;&nbsp;通过为期两天的参观考察，拓宽了我们的视野，让我们对如何开展电气专业的数字化资源库建设有了新的认识，为电气专业的示范校建设工作提供了很多的借鉴作用。（机电部电子电工组 周新妹）</span></p>
<p class="p0" style="text-align: center; margin-top: 0pt; margin-bottom: 0pt"><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes"><img alt="" width="421" height="316" src="/HrSchoolFiles/1/FckEditor/201210001/image/%E6%96%B0%E5%A6%B9%E5%86%99%E7%BB%8D%E5%85%B41.jpg" /><img alt="" width="434" height="324" src="/HrSchoolFiles/1/FckEditor/201210001/image/%E6%96%B0%E6%B2%A1%E5%86%99%E5%AE%81%E6%B3%A21.jpg" /></span></p>
<p class="p0" style="margin-top: 0pt; margin-bottom: 0pt"><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes"><o:p></o:p></span></p>
</div>
