INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('5', '0', '1', '', '17', '全面推进全省教育现代化建设', '201300001', '曹英山', '高考预科部', '', '<p>&nbsp; <span style="font-family: Simsun; font-size: 13px; line-height: 25px; ">1月17日至18日，2012年全省教育局长会议在杭州举行。副省长郑继伟出席会议并讲话。省委教育工委书记、省教育厅厅长刘希平作工作报告，省委教育工委副书记、省教育厅副厅长汪晓村作会议总结。会议强调，全省教育系统要坚持以科学发展观为指导，认真贯彻&ldquo;八八战略&rdquo;和&ldquo;创业富民、创新强省&rdquo;总战略，积极落实教育规划纲要，深化改革，促进公平，提高质量，维护稳定，着力解决教育热点难点问题，全面推进全省教育现代化建设。</span><br style="font-family: Simsun; font-size: 13px; line-height: 25px; " />
<span style="font-family: Simsun; font-size: 13px; line-height: 25px; ">　　省政府副秘书长马林云，省人大教科文卫委员会副主任委员陈永昊，省政协科教委员会副主任倪明江，省委教育工委、省教育厅领导班子成员蒋胜祥、何杏仁、鲍学军、褚子育、韩平、葛菲、葛为民、丁天乐、王玉庆，省教育工会主席李逸凡等出席会议。</span><br style="font-family: Simsun; font-size: 13px; line-height: 25px; " />
<span style="font-family: Simsun; font-size: 13px; line-height: 25px; ">　　郑继伟指出，2011年是全面实施教育改革发展规划纲要的一年。一年来，各级党委、政府深入学习贯彻全省教育工作会议精神，认真研究和谋划新时期的教育工作，积极推进教育体制改革试点，13项国家试点和31项省级试点项目进展顺利，民办教育改革、高校招生制度改革、中小学教师专业发展制度等一批试点项目取得阶段性成果。各地、各校都结合实际，积极主动地开展了一系列教育教学改革实践。作为我省民办教育改革教育试点地区之一的温州市，研究出台的试点方案，对民办教育的支持力度之大、创新举措之多，在省内外产生了较大影响。这些成绩的取得，离不开全省教育战线广大同志的辛勤劳动和无私奉献。</span><br style="font-family: Simsun; font-size: 13px; line-height: 25px; " />
<span style="font-family: Simsun; font-size: 13px; line-height: 25px; ">　　郑继伟强调，2012年是党的十八大和省第十三次党代会召开之年，是新一届县（市、区）政府开展工作的第一年，也是实施&ldquo;十二五&rdquo;规划承上启下的关键一年。全省教育系统要坚持优先发展，各级各类教育都要把提高教育质量和人才培养水平摆在更加突出的位置，注重培养学生的创造精神和实践能力；鼓励开展多层次、宽领域的教育交流与合作，积极学习借鉴国内外教育的先进经验，不断提升教育水平；加快教育信息化步伐，推进信息技术在学校教育教学、科研、管理中的广泛应用；要狠抓落实，积极深入实施教育规划纲要，各地要按照省委、省政府的部署，统筹管理好当地的各类教育，集中力量办几件人民群众满意、为教育发展增添后劲的大事；要进一步深化推进教育体制改革，把改革的思想渗透到日常的教育、教学和管理之中，引导和组织广大教师、校长不断转变教育观念，转换育人模式，全面落实素质教育的要求，真正按照教育规律办学，按照人才成长规律育人；进一步完善普通高中课程改革方案，使课程改革更符合育人需要；以开展教师资格制度和注册制度改革试点为契机，建立完善教师准入制度和退出机制，认真做好绩效工资的落实，不断激发教师勤恳教书育人的积极性和创造性；要维护大局，确保校园和谐稳定，继续以&ldquo;平安校园&rdquo;建设为抓手，深入细致地做好隐患排查、问题整改、督促巩固等工作，不断提高校园安全管理水平，根据国家部署和我省实际建立学生接送车制度，切实做好学生接送车安全工作；要多深入基层调查研究，多深入师生了解情况，保障弱势群体的教育权益，促进教育资源的优化配置，不断促进教育公平。</span><br style="font-family: Simsun; font-size: 13px; line-height: 25px; " />
<span style="font-family: Simsun; font-size: 13px; line-height: 25px; ">　　刘希平指出，2011年，全省教育系统紧紧围绕&ldquo;促进公平、提高质量、实现协调、增强活力、强化服务&rdquo;，以科学发展观为指导，深入贯彻&ldquo;八八战略&rdquo;和&ldquo;创业富民、创新强省&rdquo;总战略，大胆改革，勇于创新，扎实工作，推动教育科学和谐发展，比较好地实现了&ldquo;十二五&rdquo;第一年的要求，教育现代化建设迈出了坚实步伐；制定并形成了比较完善的教育发展规划体系，创新实施了一系列新的工作载体，争取并获得了一系列有利于教育规划实施的政策条件，全面推进深化了教育改革和开放，有效推动了一些教育难点热点问题的化解，全面完成了年初承诺的十个方面实事，切实维护了校园的和谐稳定，加强和改进了党的建设，全省教育工作继续保持了蓬勃发展的势头，为教育现代化建设开好了头、起好了步。</span><br style="font-family: Simsun; font-size: 13px; line-height: 25px; " />
<span style="font-family: Simsun; font-size: 13px; line-height: 25px; ">　　刘希平强调，新的一年，全省教育系统要肩负起崇高使命，以百倍的信心、时不我待的精神、锐意改革创新的勇气，扎实努力，奋力拼搏，一步一个脚印，朝着教育现代化奋勇前进，以全省教育改革发展的新成绩迎接党的十八大和省第十三次党代会的胜利召开。要深入研究推进育人工作。切实强化培养中国特色社会主义可靠接班人、合格建设者的意识，自觉地把社会主义核心价值体系建设贯穿于教育教学及管理的全过程，尤其是融入各类专业课教学中去。紧紧围绕迎接党的十八大，广泛开展&ldquo;科学发展、辉煌成就&rdquo;主题教育活动，推动学雷锋活动机制化、常态化，进一步繁荣校园文化。要不断提高教育发展的质量和水平。全面展开各项规划建设工作，落实学前教育三年行动计划，推进以县（市、区）为单位的义务教育均衡发展，启动实施中职教育现代化建设工程，积极贯彻全省高等教育工作座谈会精神，调整优化高等教育结构，全面启动实施&ldquo;十二五&rdquo;重点学科建设和优势专业、特色专业建设项目，深入落实特殊教育相关政策，加快实施教育信息化建设工作，继续抓好对口支援工作。要进一步加强教师队伍建设。下大力气落实中小学教师专业发展培训制度，认真做好中小学教师资格考试和定期注册、中小学教师流动、中小学教师职称制度改革等试点工作；一手抓培养培训，一手抓高层次人才引进，不断加强高校人才建设。要着力解决突出教育难题。坚定不移地推进中小学&ldquo;减负&rdquo;，出重拳治理择校乱收费顽症，切实做好普通高中&ldquo;国有民办&rdquo;学校清理规范工作和高等院校独立学院规范设置工作，进一步改善进城务工人员随迁子女就地接受义务教育的条件和环境。要大力推进教育改革开放。既要支持各地各学校大胆探索，又要加强顶层设计，集中力量抓好事关全省的重大改革。进一步深化课程改革，重点是深化普通高中课程改革，积极推进民办教育综合改革，改革完善考试招生制度，促进教学方式方法变革，建立健全评价制度，全面启动教育国际化工程。要切实保持校园和谐稳定。全面加强学生交通安全制度建设和管理，继续加强学校人防、物防、技防建设，努力把平安校园建设引向深入，进一步加强学校尤其是高校的学生工作。</span><br style="font-family: Simsun; font-size: 13px; line-height: 25px; " />
<span style="font-family: Simsun; font-size: 13px; line-height: 25px; ">　　刘希平还强调，推进全省教育现代化建设，工作在教育系统的全体党员干部尤其是领导干部肩负着很重的责任。为适应新形势新任务新要求，全省教育系统要按照胡锦涛总书记切实防范&ldquo;精神懈怠的危险、能力不足的危险、脱离群众的危险、消极腐败的危险&rdquo;的讲话精神，进一步加强全系统党员干部队伍建设，着力培养和树立远大教育理想，着力增长工作才干，着力为群众办实事，着力加强党风廉政建设。毫不放松地抓好创先争优活动，努力使创先争优长效化，成为推动工作和发展的不竭动力。</span><br style="font-family: Simsun; font-size: 13px; line-height: 25px; " />
<span style="font-family: Simsun; font-size: 13px; line-height: 25px; ">　　汪晓村在会议总结时强调，各地要加强学习研究，统一思想认识，研究教育工作的规律和本质属性，提高领导教育科学发展的能力；要真抓实干，务求实效，重点抓好治理择校乱收费等人民群众关心的热点问题，把会议提出的各项任务落到实处；要克难攻坚，勇于创新，着力破解教育发展过程中的难点和瓶颈问题，以奋发的精神，保持勇气，鼓足锐气，为全面推进教育现代化尽一份力。</span><br style="font-family: Simsun; font-size: 13px; line-height: 25px; " />
<span style="font-family: Simsun; font-size: 13px; line-height: 25px; ">　　会议表彰了获得2011年度教育科学和谐发展业绩考核优秀的单位。与会的各市、县（市、区）教育局长围绕郑继伟的讲话和刘希平的工作报告进行了认真的讨论和交流。全省高等学校负责人，省级有关部门处室负责人，省教育厅对口联系民主党派负责人，省特约教育督导员，省委教育工委、省教育厅各处室、直属单位负责人列席会议。</span></p>', '', '\HrSchoolFiles\1\UpFiles\网站内容\1\5.jpg', '', '', '11', '2', '0', '', '', '', ' ', '0', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('5', '0', '2', '', '2', '考察企业、 拜师学艺，提升电子电工专业技能', '201210001', '王吉', '机电部电子电工组', '', '<p>　&nbsp;<span style="font-size: medium">&nbsp; 2013年3月29日--30日，我校电子电工组技能辅导团的6位老师，在许建军副校长、谢华林主任的带领下前往温州市浙江亚龙教育装备股份有限公司参观考察，向企业技术骨干交流学习、拜师学艺。&nbsp; <br />
　　29日下午一行8人到达亚龙公司，受到公司领导的热情接待。公司领导首先带领我们参观了教学设备展示大厅和生产车间，看到了机电专业各种实训装备，有的是我们教学中正在使用的，有的是专业发展中的前沿装备。通过参观，各位老师在开阔专业视野的同时，也在思考如何完善我校的实训装备。&nbsp; <br />
　　交流会上，许副校长介绍了我校示范校建设的基本情况，对示范校建设中存在的问题和校企如何深入合作、校本教材开发等与公司领导作了深入的交流和讨论。亚龙公司的领导充分肯定了我校示范校的建设情况，对我校建设中存在疑惑和问题给予了热情的回答和指导，同时表明会大力支持我校的示范校建设。&nbsp; <br />
　　许副校长、谢主任提议电子电工组技能辅导团的教师向亚龙公司的企业技术骨干一对一拜师学艺，得到了公司的热情回应，简单而庄重的拜师仪式后，师徒双方就教学问题、专业技能、专业发展方向、技能大赛等问题又进行了深入的交流和讨论。&nbsp; <br />
　　浙江亚龙教育装备有限公司是为高等院校、职业院校、技师院校、公共实训中心、行业企业培训中心提供教育装备、师资培训、课程开发、教材开发、技能大赛、技能鉴定、环境设计等整体解决方案服务的企业。该公司目前已成为我国教育技术装备龙头企业、国家高新技术企业、浙江省创新型企业、温州市100家重点骨干企业。&nbsp;　 <br />
　　通过本次活动，不仅为老师们解决了技能辅导中存在的技术问题，开阔了专业视野。更为校企合作的深入发展和专业建设奠定了扎实的基础。（高考部李晶）&nbsp;</span></p>
<p style="text-align: center"><span style="font-size: medium"><img alt="" style="width: 491px; height: 273px" src="/HrSchoolFiles/1/FckEditor/201210001/image/%E6%9D%8E%E6%99%B6%E5%86%99%E4%BA%9A%E9%BE%992.jpg" /><img alt="" style="width: 491px; height: 388px" src="/HrSchoolFiles/1/FckEditor/201210001/image/%E6%9D%8E%E6%99%B6%E5%86%99%E4%BA%9A%E9%BE%991.jpg" /></span></p>', '', '\HrSchoolFiles\1\UpFiles\网站内容\2\5.jpg', '', '', '35', '2', '0', '', '', '无', '', '0', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('5', '0', '3', '', '12', '2014春夏女式运动装新兴趋势--短T恤', '200910002', '欧欣怡', '轻纺化工部服装组', '', '<p><span style="white-space: normal; text-transform: none; word-spacing: 0px; float: none; color: rgb(0,0,0); text-align: left; font: 12px/21px tahoma, arial, 宋体; orphans: 2; widows: 2; display: inline !important; letter-spacing: normal; background-color: rgb(255,255,255); text-indent: 24px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px">无领是运动风还是街头风，2014春夏的基本款运动针织T恤经过剪裁处理，运动装单品透露出运动气息。</span></p>
<p>&nbsp;</p>
<p>&nbsp;</p>
<p>&nbsp;</p>
<p>&nbsp;</p>
<p>&nbsp;</p>
<p>&nbsp;</p>
<p><img width="600" height="225" alt="" src="/HrSchoolFiles/1/FckEditor/200910002/未标题-1.jpg" /></p>
<h3 style="white-space: normal; text-transform: none; word-spacing: 0px; color: rgb(0,0,0); padding-bottom: 5px; text-align: left; padding-top: 0px; font: 700 20px/34px Georgia, ''Times New Roman'', Times, serif; padding-left: 0px; margin: 0px; orphans: 2; widows: 2; letter-spacing: normal; padding-right: 0px; background-color: rgb(255,255,255); text-indent: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px">宽松的运动T恤</h3>
<p style="white-space: normal; text-transform: none; word-spacing: 0px; color: rgb(0,0,0); padding-bottom: 4px; text-align: left; padding-top: 4px; font: 12px/21px tahoma, arial, 宋体; padding-left: 0px; margin: 0px; orphans: 2; widows: 2; letter-spacing: normal; padding-right: 0px; background-color: rgb(255,255,255); text-indent: 2em; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px">经典的圆领T恤通过进行简单的裁剪，成为2014春夏主要款式，是健身、普拉提和跳舞等运动的理想之选。160-190gms左右的轻盈棉布打造的T恤可以轻易叠搭在文胸式上衣或紧身连衣裤之外穿着。该经典的修身短上衣简单而宽松，重点突出了简约的细节和印花设计，其中，140-160gms轻质运动针织呈现的效果最佳。</p>
<p><img width="600" height="236" alt="" src="/HrSchoolFiles/1/FckEditor/200910002/7（2）.jpg" /></p>
<h3 style="white-space: normal; text-transform: none; word-spacing: 0px; color: rgb(0,0,0); padding-bottom: 5px; text-align: left; padding-top: 0px; font: 700 20px/34px Georgia, ''Times New Roman'', Times, serif; padding-left: 0px; margin: 0px; orphans: 2; widows: 2; letter-spacing: normal; padding-right: 0px; background-color: rgb(255,255,255); text-indent: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px">最爱品牌</h3>
<p style="white-space: normal; text-transform: none; word-spacing: 0px; color: rgb(0,0,0); padding-bottom: 4px; text-align: left; padding-top: 4px; font: 12px/21px tahoma, arial, 宋体; padding-left: 0px; margin: 0px; orphans: 2; widows: 2; letter-spacing: normal; padding-right: 0px; background-color: rgb(255,255,255); text-indent: 2em; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px">大尺寸的品牌商标是关键，其中，经典的运动品牌散发出纯正的20世纪90年代风格气息。前端的打结设计和定制的流苏下摆，尽显复古质感。面料以简洁的190gms单棉面料为主。</p>
<p><img width="600" height="239" alt="" src="/HrSchoolFiles/1/FckEditor/200910002/8（2）.jpg" /></p>
<h3 style="white-space: normal; text-transform: none; word-spacing: 0px; color: rgb(0,0,0); padding-bottom: 5px; text-align: left; padding-top: 0px; font: 700 20px/34px Georgia, ''Times New Roman'', Times, serif; padding-left: 0px; margin: 0px; orphans: 2; widows: 2; letter-spacing: normal; padding-right: 0px; background-color: rgb(255,255,255); text-indent: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px">美国全明星阵容</h3>
<p style="white-space: normal; text-transform: none; word-spacing: 0px; color: rgb(0,0,0); padding-bottom: 4px; text-align: left; padding-top: 4px; font: 12px/21px tahoma, arial, 宋体; padding-left: 0px; margin: 0px; orphans: 2; widows: 2; letter-spacing: normal; padding-right: 0px; background-color: rgb(255,255,255); text-indent: 2em; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px">足球、棒球等美国运动元素和风格是2014春夏的重要主题。设计师们将球队的标志、大尺寸的球服号码作用在基本款运动针织上。另外，网纱镶嵌和拼接设计演绎出纯正的赛场外观。</p>
<p><img width="600" height="238" alt="" src="/HrSchoolFiles/1/FckEditor/200910002/9（2）.jpg" /></p>
<h3 style="white-space: normal; text-transform: none; word-spacing: 0px; color: rgb(0,0,0); padding-bottom: 5px; text-align: left; padding-top: 0px; font: 700 20px/34px Georgia, ''Times New Roman'', Times, serif; padding-left: 0px; margin: 0px; orphans: 2; widows: 2; letter-spacing: normal; padding-right: 0px; background-color: rgb(255,255,255); text-indent: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px">短背心</h3>
<p style="white-space: normal; text-transform: none; word-spacing: 0px; color: rgb(0,0,0); padding-bottom: 4px; text-align: left; padding-top: 4px; font: 12px/21px tahoma, arial, 宋体; padding-left: 0px; margin: 0px; orphans: 2; widows: 2; letter-spacing: normal; padding-right: 0px; background-color: rgb(255,255,255); text-indent: 2em; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px">易于穿着的背心游走在运动装、街头装和沙滩装之间。高于肚脐的修身款式中贴合的袖口和宽松的下摆，使其成为叠搭在文胸式上衣或比基尼之外的理想单品。背部的打结设计颇具运动质感。140gms左右的超轻盈运动针织有着平纹表面或粗放针织，成就了透明的效果。</p>
<p><img width="600" height="240" alt="" src="/HrSchoolFiles/1/FckEditor/200910002/10（2）.jpg" /></p>
<h3 style="white-space: normal; text-transform: none; word-spacing: 0px; color: rgb(0,0,0); padding-bottom: 5px; text-align: left; padding-top: 0px; font: 700 20px/34px Georgia, ''Times New Roman'', Times, serif; padding-left: 0px; margin: 0px; orphans: 2; widows: 2; letter-spacing: normal; padding-right: 0px; background-color: rgb(255,255,255); text-indent: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px">长袖修身T恤</h3>
<p style="white-space: normal; text-transform: none; word-spacing: 0px; color: rgb(0,0,0); padding-bottom: 4px; text-align: left; padding-top: 4px; font: 12px/21px tahoma, arial, 宋体; padding-left: 0px; margin: 0px; orphans: 2; widows: 2; letter-spacing: normal; padding-right: 0px; background-color: rgb(255,255,255); text-indent: 2em; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px">中等质量的运动T恤无疑是普拉提、瑜伽和舞蹈等的理想之选。为了打造出更方便运动且更具支持作用的修身T恤，设计师们采用了190-200gms的弹性棉线混纺。经过背部毛圈处理的厚实运动针织，取代了叠搭的热身单品和夏日汗衫。</p>
<p><img width="600" height="244" alt="" src="/HrSchoolFiles/1/FckEditor/200910002/11（2）.jpg" /></p>
<p>&nbsp;</p>', '', '\HrSchoolFiles\1\UpFiles\网站内容\3\5.jpg', '', '', '3', '2', '0', '', '', '', '', '0', '0', '0')
