INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('6', '0', '3', '', '8', '服饰文化节布贴画作品展', '200110002', '郝红花', '轻纺化工部部长室', '', '', '', '\HrSchoolFiles\1\UpFiles\网站内容\3\6.jpg', '', '', '1', '2', '0', '', '', '', '', '0', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('6', '0', '7', '', '12', 'adad', '99', '王骥', '教务处', '', '<p>&nbsp;1111111111111</p>', '', '', '', '', '1', '2', '0', '', '', '', '', '0', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('7', '0', '1', '', '18', '健康之花，在运动中绽放', '201300001', '戚群', '校务办', '', '<p>&nbsp;<span style="font-family: Simsun; font-size: 13px; line-height: 25px; ">&ldquo;人间四月芳菲尽，山寺桃花始盛开&rdquo;，4月3日下午2点45分，我校全体教职工19个工会小组，共270余人齐聚体艺中心东侧篮球场。此时，虽没有旌旗招展，但在这一刻，每个人都可以放下紧张的工作；在这一刻，每个人都洋溢着开心的表情；在这一刻，每个人都已整装待发。2013年全校教职工健身运动会正式拉开了序幕。</span><span style="font-family: Simsun; font-size: 13px; line-height: 25px; ">&nbsp;</span></p>
<p style="font-family: Simsun; font-size: 13px; line-height: 25px; ">　　本次比赛共设9个项目：2000米健身走、羽毛球比赛、乒乓球比赛、一分钟跳绳、一分钟踢毽子、排球比赛、篮球半场比赛、足球比赛、定点投篮。大家根据自己的兴趣参与不同的项目。赛前，张书记满怀激情地为此次运动会作了动员。张书记希望大家能够在此次健身运动会中赛出友谊，赛出团结，更能赛出职中人的精神。&nbsp;<br />
微风拂面，垂柳依依。阳春的赛场，有吐不尽的韵味。&nbsp;<br />
瞧，八十人的健走爱好者穿上运动鞋，已经站在跑道上。翟振平老师正用高音喇叭宣布比赛规则：&ldquo;&nbsp;按照规定每人必须走5圈，用时最短者获冠军。&rdquo;随着石静老师的一声令下，浩荡的队伍踏上赛道。起初，每个人都认为2000米健身走相对比较简单，但走起来还着实让人有些吃力。行走第一、二圈的时候，每个人还都能跟紧步伐。但当走第三圈的时候，许多人便感到有些气喘吁吁。有的老师逐渐落在队伍后面，于是就并驾齐驱，结伴而行，相互激励着迎头赶上。&nbsp;&ldquo;老骥伏枥，志在千里&rdquo;，年纪稍大的老师表现的一点也不比年轻人逊色，竟能一直赶在队伍前头带领大家奔向终点。<br />
看，小小的乒乓球如精灵般在赛场上飞舞。但在有实力的男老师眼里却如鱼得水。这阵势真有种临危不乱，大杀四方之感。有的老师说：&ldquo;这真是一种技术活儿。&rdquo;&nbsp;驻足羽毛球赛场。白色的羽毛球在拦网两端掠过。老师们矫健的身手，时而大开大合，宛如雄鹰搏空；时而刚中带柔，似是江南小雨。虽然男老师可能占优势，但是女老师也不甘示弱，有巾帼不让须眉的豪气。定点投篮的赛场更是热闹。有的老师连投了五个球，只进了一个。站在一旁观战的老师们仍然都带着微笑为之喝彩加油。一分钟跳绳，时间短，但若想多跳几个，还真有点难。跳绳在女老师的手中却总能在空中划出一道道美丽的弧线。&nbsp;<br />
另外在排球、足球的赛场上，更多体现的是默契的配合，激烈的跑动。汗水不时地顺着两颊留下。每当一方多赢一个球时，内心涌上的那种喜悦总是让人忘掉所有的疲惫。相信在这绿荫场地上总能让人寻找到学生时代拼搏的足迹。&nbsp;<br />
时间如奔涌的泉水在运动中流淌的很快。回望赛场，也许这儿充满了竞争，但同事之间却结下了亲人般的友谊。也许你的名次并不佳，但这足够引起你对自身健康的重视。也许你有几分遗憾，但收获更多的是宽容与和谐。全体教职工在此次运动会上充分展现了职中人的精神风貌，更展现了师者之风范。&nbsp;<br />
&ldquo;一支粉笔写春秋，三尺讲台育英杰&rdquo;，但在此时，全体教职工绽放的是别样的风采。&nbsp;（&nbsp;工会&nbsp;&nbsp;姜云波）&nbsp;<br />
