INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('9', '0', '1', '', '21', '领取2013全国英语等级考试(PETS)准考证通知', '99', '曹英山', '高考预科部', '', '<p>&nbsp;<span style="font-family: Simsun; font-size: 13px; line-height: 25px; ">2013上半年全国英语等级考试准考证已到.请到高考部英语组(5楼) 曹英山老师处领取.本次考试笔试地点在我校,口试地点在城关中学.考试时间: 笔试上午8:30入场, 8:45禁止入场,9:00开考, 下午口试1:30入场,2:00开考. 入场时需准考证及有效身份证件(身份证,没有身份证的,有照片的胸卡也可). 请带好2B铅笔,黑色水笔,不得携带手机等通讯工具.&nbsp;</span></p>
<p style="font-family: Simsun; font-size: 13px; line-height: 25px; ">另外2012年9月份考试通过的证书还有部分同学没有领取, 涉及班级原一年17班,18班 32班,二年35班.请班主任派同学领取.</p>
<p style="font-family: Simsun; font-size: 13px; line-height: 25px; ">另外 周三\周四\周五中午将在科技楼语音室辅导听力与口试, 请参加考试同学有时间参加.特别是第一次考试的同学争取一定要参加.</p>', '', '', '', '', '7', '2', '0', '', '', '', ' ', '0', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('9', '0', '2', '', '16', '项建峰简介', '201300003', '沈凯超', '机电技术部电子电工组', '', '<p style="text-align: center"><img width="324" height="469" alt="" src="/HrSchoolFiles/1/FckEditor/201300003/项建峰.jpg" /></p>
<p style="text-align: center">&nbsp;</p>
<p class="MsoNormal" style="margin: 0cm 0cm 0pt"><b style="mso-bidi-font-weight: normal"><span style="font-size: 12pt; font-family: 宋体">项建峰：</span></b><span style="font-size: 12pt; font-family: 宋体">男，中共党员，毕业于浙江师范大学，本科学历，应用电子技术教育专业。中学二级教师，无线电调试高级工。现主要任教<span lang="EN-US">Protel</span>及数控维修仿真学科。<span lang="EN-US"><o:p></o:p></span></span></p>
<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 24pt"><span style="font-size: 12pt; font-family: 宋体">项老师任教以来总是接手专业的新开课程，在教学过程中积极探索、勇于接受挑战。曾获嘉兴市中职青年教师技能比武专业基础知识竞赛一等奖；嘉兴市<span lang="EN-US">&ldquo;</span>天煌杯<span lang="EN-US">&rdquo;</span>维修电工竞赛第一名；浙江省中等职业学校<span lang="EN-US">&ldquo;</span>维修电工<span lang="EN-US">&rdquo;</span>技能竞赛二等奖；嘉兴市第五届技能节<span lang="EN-US">&ldquo;</span>亚龙杯<span lang="EN-US">&rdquo;</span>电子产品装接与调试项目教师组一等奖（第一名）；<span lang="EN-US">2010</span>年度校优秀党员；<span lang="EN-US">2011</span>年度校敬精明星奖；<span lang="EN-US">2012</span>年度校精研奖。<span lang="EN-US"><o:p></o:p></span></span></p>
<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 24pt"><span style="font-size: 12pt; font-family: 宋体">在技能大赛方面，项老师所带学生曾多次在省市以及全国级比赛中获奖，其中金黎杰、徐家同学先后获得全国中职技能竞赛<span lang="EN-US">&ldquo;</span>亚龙杯<span lang="EN-US">&rdquo;</span>电子装接与调试项目竞赛三等奖。自己也多次被评为嘉兴市优秀指导教师。<span lang="EN-US"><o:p></o:p></span></span></p>
<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 24pt"><span style="font-size: 12pt; font-family: 宋体">所写论文获嘉兴市职教论文评比二等奖<span lang="EN-US">1</span>篇；三等奖<span lang="EN-US">2</span>篇。同时参与编写校本教材《数控机床维修仿真实训》以及《<span lang="EN-US">PLC</span>控制技术与技能训练》并由清华大学出版社出版；同时参与浙江省普通高中网络选修课程《声光控电路设计与制作》的开发。<span lang="EN-US"><o:p></o:p></span></span></p>
<p style="text-align: left">&nbsp;</p>', '', '', '', '', '18', '2', '0', '', '', '', '', '0', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('9', '0', '3', '', '26', '我校召开2007届毕业生就业招聘会', '200910002', '服装创新基地委员会', '轻纺化工部服装组', '', '<p style="margin: 5pt 3.75pt 5pt 3.8pt; line-height: 150%; text-indent: 24pt; mso-char-indent-count: 2.0; mso-para-margin-top: 5.0pt; mso-para-margin-right: 3.75pt; mso-para-margin-bottom: 5.0pt; mso-para-margin-left: .36gd">&nbsp;</p>
<p style="margin: 5pt 3.75pt 5pt 3.8pt; line-height: 150%; text-indent: 24pt; mso-char-indent-count: 2.0; mso-para-margin-top: 5.0pt; mso-para-margin-right: 3.75pt; mso-para-margin-bottom: 5.0pt; mso-para-margin-left: .36gd"><span style="color: black"><font size="3"><font face="宋体">为进一步加强校企交流，使企业找到满意的人才，学生找到合适自己的岗位，同时也为了做好我校<span lang="EN-US">2007</span>届毕业学生就业工作，<st1:chsdate w:st="on" year="2013" month="12" day="21" islunardate="False" isrocdate="False"><span lang="EN-US">12</span>月<span lang="EN-US">21</span>日下午</st1:chsdate>，我校<span lang="EN-US">2007</span>届毕业生校园专场招聘会在校体艺中心举行，景兴、科宝、罗马、金建峰等<span lang="EN-US">30</span>余家用人单位进场设摊，为本届毕业生提供<span lang="EN-US">330</span>多个就业岗位。<span lang="EN-US"><o:p></o:p></span></font></font></span></p>
<p style="margin: 5pt 3.75pt; line-height: 150%"><font size="3"><font face="宋体"><span lang="EN-US" style="color: black">&nbsp;&nbsp;&nbsp; </span><span style="color: black">招聘会上，毕业生认真查看岗位需求信息，携自荐材料与意向的单位进行洽谈。学校领导也莅临现场指导。本次招聘除我校学生外，还吸引了不少社会应聘人员参加。据悉，当天共有<span lang="EN-US">280</span>余位学生参加了招聘会，部分毕业生与招聘单位达成就业意向。<span lang="EN-US"><o:p></o:p></span></span></font></font></p>
<p style="margin: 5pt 3.75pt; line-height: 150%"><font size="3"><font face="宋体"><span lang="EN-US" style="color: black">&nbsp;&nbsp;&nbsp; </span><span style="color: black">此次招聘会的举行不但为毕业生提供了对口的岗位招聘信息，方便毕业生应聘洽谈，同时进一步加强了学校与各企事业单位的沟通和了解，也利于学校掌握人才市场需求方向，进而推动以促进学生技能培养、提高就业能力为重点的教育教学改革。<span lang="EN-US"><o:p></o:p></span></span></font></font></p>
<p style="margin: 5pt 3.75pt; line-height: 150%"><font size="3"><font face="宋体"><span lang="EN-US" style="color: black">&nbsp;&nbsp;&nbsp; </span><span style="color: black">招聘会上，我校高三服装专业毕业学生还为各位来宾展示了由自己设计，自己制作的<span lang="EN-US">12</span>个系列的<span lang="EN-US">60</span>余套服装作品。这些作品充分展示了我校服装设计专业学生的设计和制作技能，彰显了年轻、活力、个性的时代气息，这是他们走向实习岗位前向母校献上的一份礼物。<span lang="EN-US"><o:p></o:p></span></span></font></font></p>
<p>&nbsp;</p>', '', '', '', '', '0', '2', '0', '', '', '', '', '0', '0', '0')
