INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('10', '0', '1', '', '22', '2013年省中职化工专业教研大组理事会', '201300001', '公共账户', '公共部门', '', '<p>&nbsp;<span style="font-family: Simsun; font-size: 13px; line-height: 25px; ">2013年省中职化工专业教研大组理事会</span></p>
<p style="font-family: Simsun; font-size: 13px; line-height: 25px; ">在金华市浙江商贸学校召开</p>
<p style="font-family: Simsun; font-size: 13px; line-height: 25px; ">4月2日在浙江商贸学校召开2013年浙江省中职化工专业教研大组理事会，参会成员有省内各市教研员和中职化工类专业理事学校的教师代表。</p>
<p style="font-family: Simsun; font-size: 13px; line-height: 25px; ">本次会议有省教科院职成教教研室教研员庞志康老师主持。</p>
<p style="font-family: Simsun; font-size: 13px; line-height: 25px; ">会议的主要内容有：&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>
<p style="font-family: Simsun; font-size: 13px; line-height: 25px; ">&nbsp;&nbsp;&nbsp;&nbsp;1、研讨化工专业课改教材编写工作；<br />
&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;2、研讨今年全省中职学校学生技能竞赛相关事宜。</p>
<p style="font-family: Simsun; font-size: 13px; line-height: 25px; ">会上浙江商贸学校的王书记介绍了学校的发展概况；杭州市良渚职业中专的陈雪校老师专门解读了浙江省化工专业课程改革教材编写工作的要点和注意事项，平湖市职业中专和绍兴市职业中专相应承担无机化工原料和化工分析两本教材的引领编纂工作；随后杭州职业技术学院化工学院的何艺副院长等三位老师专门解读了2013年全国技能大赛暨浙江省技能选拔赛的工业分析检测、化工生产过程控制、化工仪表自动化和化工设备维修等赛项规程的相关事宜。与会代表进行了热烈的讨论并提出一些建设性的意见和建议。最后省教研员庞志康老师作了总结性发言，庞老师希望大家在百忙之中对省课改教材编写的文件进行学习，对本次省技能大赛选拔赛要充分准备，祝愿大家赛出成绩、赛出水平。</p>
<p style="font-family: Simsun; font-size: 13px; line-height: 25px; ">我校朱玉林、许新福、沈佳渊三位老师参加了本次会议。</p>
<p style="font-family: Simsun; font-size: 13px; line-height: 25px; ">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>
<p style="font-family: Simsun; font-size: 13px; line-height: 25px; ">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 化工组&nbsp;&nbsp; 许新福&nbsp; 文</p>
<p style="font-family: Simsun; font-size: 13px; line-height: 25px; ">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2013、4、5</p>', '', '\HrSchoolFiles\1\UpFiles\网站内容\1\10.jpg', '', '', '1', '2', '0', '', '', '', ' ', '0', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('10', '0', '2', '', '16', '陆元杰简介', '201300003', '沈凯超', '机电技术部电子电工组', '', '<p style="text-align: center"><img alt="" src="/HrSchoolFiles/1/FckEditor/201300003/陆元杰.jpg" /></p>
<p style="text-align: center">&nbsp;</p>
<p class="MsoNormal" style="margin: 0cm 0cm 0pt"><b style="mso-bidi-font-weight: normal"><span style="font-size: 12pt; font-family: 宋体">陆元杰：</span></b><span style="font-size: 12pt; font-family: 宋体">男，毕业于东北财经，本科学历，工程管理专业。中学二级教师，维修电工技师。主要任教<span lang="EN-US">PLC</span>学科。<span lang="EN-US"><o:p></o:p></span></span></p>
