INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('20', '0', '2', '', '3', '重要通知', '201210001', '王吉', '机电技术部电子电工组', '', '<p>重要通知：机电部电子电工组各位教师，请大家于6月20日之前将自己2012学年的公开课教案及教学设计放在ftp机电部/电子电工组/公开课与教学设计文件夹中，谢谢配合！</p>', '', '', '', '', '36', '2', '0', '', '', '', '', '0', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('20', '0', '3', '', '26', '办好“同创衣家”，丰富“两创”内涵', '200910002', '服装创新基地委员会 ', '轻纺化工部服装组', '', '<p>&nbsp;</p>
<p>&nbsp;&nbsp; 3月21日，经贸艺术部在部会议室召开了服装组工作会议，贺陆军校长、经贸艺术部杨玉华主任、总务处史美芳副主任参加了会议。会议就&ldquo;同创衣家&rdquo;开业后的经营情况和如何利用这一基地推进服装专业教学改革进行了细致的分析与热烈的探讨。</p>
<p>&nbsp;</p>
<p>&nbsp;&nbsp;&nbsp; 会上，顾雪娟老师首先汇报了&ldquo;同创衣家&rdquo;开业以来的经营情况，服装组组长杨春华就如何利用&ldquo;两创基地&rdquo;推进服装专业教学改革交流了组内的工作设想。大家对&ldquo;同创衣家&rdquo; 的经营模式正确定位，提出了新的经营策略，拟定了学生自主制作服装的初步计划。</p>
<p>&nbsp;</p>
<p>&nbsp;&nbsp;&nbsp; 会议务实、高效，大家心往一处想，劲往一处使，教师们对如何建设好、利用好&ldquo;同创衣家&rdquo;&ldquo;两创基地&rdquo;各抒己见，提出了不少宝贵的意见和建议，并达成共识。贺陆军校长一一解答了教师们提出的问题，对&ldquo;两创基地&rdquo;工作中遇到的困难指明了具体的应对措施，并对基地的各项工作进一步作了落实分工。</p>
<p>&nbsp;</p>
<p>&nbsp;&nbsp;&nbsp; 相信在校领导和服装组老师的共同努力下，&ldquo;同创衣家&rdquo;一定会越办越好，如出水芙蓉活力四射，成为服装专业学生创业、创新、大显身手的最佳平台！</p>
<p>&nbsp;</p>', '', '', '', '', '1', '2', '0', '', '', '', '', '0', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('21', '0', '2', '', '60', '接触器联锁正反转控制线路', '201210002', '沈翠婷', '机电技术部电子电工组', '', '', '', '', '接触器联锁正反转控制线路课件.ppt', '201306211154223435145226708500接触器联锁正反转控制线路课件.ppt', '20', '2', '0', '', '', '', '', '0', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('21', '0', '3', '', '26', '校企共建服务中心 师生同展技术专长――市职业中专服装制版、封样服务中心成立侧记', '200910002', '服装创新基地委员会 ', '轻纺化工部服装组', '', '<p>&nbsp;</p>
<p>&nbsp;&nbsp;&nbsp; 日前，市职业中专与平湖凌腾贸易有限公司签署合作协议，双方将共同开展服装制版、封样业务。这也标志着由市职业中专投入资金、人力，并由双方共同参与的服装制版、封样服务中心的正式启动。</p>
<p>&nbsp;</p>
<p>&nbsp;&nbsp;&nbsp; 据悉，平湖凌腾贸易有限公司是一家以承接订单、看样订货为主要形式的外贸公司，其业务主要面向欧美，我市不少服装企业均是该公司的生产合作企业。而此次公司积极推进与市职业中专的合作，其主要意向是希望借助该校服装专业雄厚的师资、技术力量及专业设施设备实现公司的&ldquo;低成本、高质量、高效率&rdquo;的发展要求，将公司业务中的服装制版、封样环节以外包的形式委托市职业中专服装制版、封样服务中心完成。</p>
<p>&nbsp;</p>
<p>&nbsp;&nbsp;&nbsp; 根据双方的协议，在市职业中专的技协服务部（由市职业中专工会出资，具有独立法人资格，专门面向社会开展各种技术服务）下设服装制版、封样服务中心，中心主要对平湖凌腾贸易有限公司开展相关技术服务，而凌腾公司将长期向服务中心提供充足的业务量。</p>
<p>&nbsp;</p>
<p>&nbsp;&nbsp;&nbsp; 为了确保效率及技术服务质量，市职业中专常年安排一名服装专任教师、一名服装实习指导教师作为服务中心工作人员；同时，学校还将业务内容融入服装专业学生的课堂教学，指导学生在参与设计、制作的过程中提高他们的服装制版、封样技术。</p>
<p>&nbsp;</p>
<p>&nbsp;&nbsp;&nbsp; 服装制版、封样服务中心也是市职业中专近年来积极推行专业建设&ldquo;产、学、研&rdquo;一体化的又一重要举措，也是服装专业继&ldquo;服装制作加工中心&rdquo;、&ldquo;服装制版技术培训中心&rdquo;、&ldquo;同创衣家实践基地&rdquo;之后的又一个师生技术发展的重要平台。相信随着这些平台建设的逐渐深入，市职业中专的师生们将可在更广阔的空间中施展才华、发展技能、提升素质。</p>
<p>&nbsp;</p>', '', '', '', '', '0', '2', '0', '', '', '', '', '0', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('22', '0', '2', '', '51', '接触器联锁正反转控制线路', '201210002', '沈翠婷', '机电技术部电子电工组', '', '', '', '', '接触器联锁正反转控制线路学案.doc', '201306210140398592204845344500接触器联锁正反转控制线路学案.doc', '19', '2', '0', '', '', '', '', '0', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('22', '0', '3', '', '26', '我校服装专业受“教育环浙行”记者团专题采访', '200910002', '服装创新基地委员会 ', '轻纺化工部服装组', '', '<p>&nbsp;</p>
<p>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; 我校省示范性专业之一&mdash;&mdash;服装专业以其强大的师资队伍、优良的教学水平、畅通的就业渠道在全省范围内已小有名气。日前，由新华社浙江分社、中国青年报浙江记者站、中国教育报浙江记者站、中新社浙江分社、浙江日报、浙江卫视等15家省级以上媒体的16位记者组成的&ldquo;教育环浙行&rdquo;记者团瞄准了我校的服装专业，并于4月15日下午，在浙江省教育厅办公室副主任陈峰、办公室主任科员陈红及平湖市副市长顾玉峰、嘉兴平湖两级教育局领导的陪同下前来我校进行专题采访。</p>
<p>&nbsp;</p>
<p>　　记者团成员之一的浙江卫视记者实地参观了我校服装专业实训工场，并通过采访教育局成职教科孙国强科长、我校张喜峰书记及服装专业的有关老师、学生，深入了解我校服装专业的办学历史、师资队伍、教学装备、教学改革、学生就业现状等情况。有关记者充分肯定了我校服装专业所取得的成绩，并决定将该专业作为我校&ldquo;一校一品&rdquo;的代表专业在浙江卫视有关栏目播放。</p>
<p>&nbsp;</p>
<p>　　通过本次采访活动，促进了我校尤其是我校服装专业的品牌建设。我们有信心，在上级领导的关心支持下，我校各专业一定会办得更好、更强，进一步发挥我校为地方经济服务的职教功能。</p>
<p>&nbsp;</p>', '', '', '', '', '0', '2', '0', '', '', '', '', '0', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('23', '0', '2', '', '7', '爱心寿星：百岁老人为灾区捐款', '99', '王骥', '教务处', '', '<p style="margin: 0px 0px 20px; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px;"><strong>沈阳百岁老人为地震灾区捐款引发质疑。记者调查发现&mdash;&mdash;</strong></p>
<p style="margin: 0px 0px 20px; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px;"><strong>　　捐款老人不是&ldquo;托&rdquo;(热点解读)</strong></p>
<p style="margin: 0px 0px 20px; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px;">　　近日，沈阳百岁老人关英汉为芦山地震灾区捐出全部存款，一时成为焦点人物。一些网友赞其为&ldquo;最美爷爷&rdquo;，也有网友质疑老人曾多次捐出&ldquo;毕生积蓄&rdquo;，可能涉嫌慈善造假，甚至有网民怀疑老人是给红十字会&ldquo;当托&rdquo;、&ldquo;当枪使&rdquo;。日前，记者来到关英汉老人家中，采访老人和他的亲人、邻里，探询事情真相。</p>
<p style="margin: 0px 0px 20px; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px;">　<strong>　朴素节俭，3年攒下10万元捐给灾区</strong></p>
<p style="margin: 0px 0px 20px; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px;">　　日前，一条微博在网上引起广泛关注。微博称：&ldquo;十万三千余元，是沈阳百岁老人关英汉近年全部存款。24日，老人颤颤巍巍把钱交到沈阳沈河区红十字会工作人员手中。老人反复叮嘱：&lsquo;钱要数清楚，一定要帮到在震区受苦的老百姓啊！&rsquo;&rdquo;</p>
<p style="margin: 0px 0px 20px; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px;">　　微博受到网民热议，许多网友被老人的善行感动，认为老人善行可敬、真情不可辜负。但也有网友提出质疑，认为老人收入不高却捐出巨款可能涉嫌慈善造假。&ldquo;报道说关老人20年捐了30多万元，他退休金不足2000元，哪能弄到如此多的钱来捐献，是生财有道还是&lsquo;当托&rsquo;造假？&rdquo;</p>
<p style="margin: 0px 0px 20px; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px;">　　带着网民疑问，记者30日来到关英汉老人家中了解情况。关英汉老人家住沈阳市沈河区一栋回迁楼里，房子仅30余平方米，屋内陈设极为简单。家具仅有两张单人床和一个老式大衣柜，挂满半面墙的家庭合影几乎成了家里唯一的装饰。</p>
<p style="margin: 0px 0px 20px; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px;">　　记者在关老家中见到了沈河区红十字会开具的&ldquo;公益事业捐赠统一票据&rdquo;，票据上注明沈河区红十字会收到关英汉捐款103324.2元。</p>
<p style="margin: 0px 0px 20px; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px;">　　那么，老人哪来巨款捐助？老人的大女儿关宗秀告诉记者，老人退休后并不缺钱，这次捐的10万多元是近3年攒下的，&ldquo;他每个月退休金2400余元，还有200元的高龄补贴。并且，老爷子已是四世同堂，共有8个子女，全家60多口人，孩孙们每月都会给老人不少&lsquo;孝敬钱&rsquo;，逢年过节还要单给。加上他很节省，一年攒下个三四万元钱一点都不奇怪。&rdquo;</p>
<p style="margin: 0px 0px 20px; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px;">　　老人的朴素节俭确实远近皆知。照顾关老的保姆杨雅琴介绍：&ldquo;老人平时生活特别节俭，用水用电都很节省。每天买菜不超过3元钱，最喜欢吃白菜炖豆腐。&rdquo;</p>
<p style="margin: 0px 0px 20px; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px;">　　住同一社区的一位大婶说，&ldquo;老爷子是大好人，平时不舍得吃穿，保姆买得最多的就是豆腐。他省下钱捐给灾区，平常人可做不到。&rdquo;</p>
<p style="margin: 0px 0px 20px; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px;"><strong>　　&ldquo;捐出毕生积蓄&rdquo;系误传</strong></p>
<p style="margin: 0px 0px 20px; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px;">　　&ldquo;不到10年，关英汉4次捐出&lsquo;毕生积蓄&rsquo;，难道是个职业捐款托？&rdquo;面对这类网上质疑，关老表示自己从未宣扬捐献了毕生积蓄，但每次确实是捐出了手头的全部存款。&ldquo;我捐款只是希望能帮助灾民，没想出名得利，也没受到过指令要求。&rdquo;</p>
<p style="margin: 0px 0px 20px; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px;">　　采访时，老人虽然高龄，但思维很敏捷，对每一笔捐款也都印象深刻。&ldquo;我一共捐过8次钱，每一次都是把手头的存款全捐了。这回为震区捐出的103000多块钱是3年前为沈河区朱剪炉街道慈善分会捐款70400元后我攒下的全部存款和利息。&rdquo;老人说，平时自己常用放大镜看看报纸标题，芦山地震的消息就是从报纸上看到的。&ldquo;我看到报纸上写灾区好多人还住在露天里，就想赶紧帮帮他们，我不能亲自去四川，能做的只有捐点钱。&rdquo;</p>
<p style="margin: 0px 0px 20px; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px;">　　关英汉老人说，是社区干部帮忙联系了红十字会，他才能在家里办上捐款手续。&ldquo;虽然捐出存款，但我儿女孝顺，还有退休金，看病又有医保，并不会影响生活。&rdquo;</p>
<p style="margin: 0px 0px 20px; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px;">　　那么，网上盛传&ldquo;多次捐出毕生积蓄&rdquo;一说从何而来？沈阳市沈河区红十字会工作人员侯有恒介绍，老人8次捐款，有两次捐给红十字会。老人1991年第一次捐款捐出全部积蓄，之后每次捐款都是捐出手中全部存款。</p>
<p style="margin: 0px 0px 20px; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px;">　　&ldquo;关老是2010年感动沈阳十大人物之一，捐款引起媒体关注并不奇怪。但沈河区红十字会从未宣传老人捐出&lsquo;毕生积蓄&rsquo;，也从未主动向媒体通报关老人捐出&lsquo;毕生积蓄&rsquo;。&rdquo;侯有恒认为，&ldquo;多次捐出毕生积蓄&rdquo;有可能是网络传播过程中出现以讹传讹。</p>
<p style="margin: 0px 0px 20px; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px;">　　记者调查发现，关于老人24日捐款的报道，大部分媒体选用&ldquo;近年来全部存款和利息&rdquo;&ldquo;存折清零&rdquo;等描述捐款事实，唯有一家网络媒体以《沈阳百岁老人为灾区捐出毕生积蓄10万元》为题进行了报道。微博传言&ldquo;老人捐出毕生积蓄被屡次报道&rdquo;的情况并不属实。<span style="widows: 2; font-family: 宋体; orphans: 2; color: rgb(0, 0, 0); -webkit-text-size-adjust: none;">(据新华社记者初抗报道，郑孝然参与采写)</span>　</p>', '', '\HrSchoolFiles\1\UpFiles\网站内容\2\2.jpg', '', '', '17', '2', '0', '', '', '无', '<p><span style="color: rgb(51, 51, 51); font-size: 14px; line-height: 25px;">近日，沈阳百岁老人关英汉为芦山地震灾区捐出全部存款，一时成为焦点人物。一些网友赞其为&ldquo;最美爷爷&rdquo;，也有网友质疑老人曾多次捐出&ldquo;毕生积蓄&rdquo;，可能涉嫌慈善造假，甚至有网民怀疑老人是给红十字会&ldquo;当托&rdquo;、&ldquo;当枪使&rdquo;。日前，记者来到关英汉老人家中，采访老人和他的亲人、邻里，探询事情真相。</span></p>', '0', '0', '0')
