INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('34', '0', '2', '', '10', '共同研讨法在《电力拖动控制线路》教学中的应用', '200310033', '周新妹', '电子电工组', '', '', '', '', '', '', '27', '2', '0', '', '', '无', '<div style="line-height: 20pt">
<div style="line-height: 20pt; text-indent: 21pt">摘&nbsp;要：《电力拖动控制线路》是中等职业技术学校电工类专业理论与实习一体化教材，同样是中级维修电工考级的重点内容。在该课程的教学中，理论教学具有一定的难度，因此选择合理的教学方法显得尤为重要。共同研讨法在本课程的一些任务教学中，能够让每位学生都参与进来，提出自己的想法，为整个任务的完成贡献自己的一份力量，在更好掌握理论的同时，为技能的训练奠定了基础，并且增强了学生之间的团结合作精神。</div>
</div>', '1', '0', '0')
