INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('36', '0', '3', '', '4', '柴冬梅简介', '200910002', '服装创新基地委员会', '服装技术部', '', '<p><img alt="" width="600" height="450" src="/HrSchoolFiles/1/FckEditor/200910002/柴冬梅.jpg" /></p>
<p>&nbsp;</p>
<div align="left"><span style="font-size: small">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 柴冬梅：1984年1月出生，毕业于浙江理工大学服装艺术设计专业，大学本科学历，文学学士学位，中教二级，服装缝制设计高级工。从教多年以来，有着6年的副班主任经验，一直担任服装专业教学工作。坚持&ldquo;勤&rdquo;字开头，潜心研究教材教法，虚心学习，精心备课、上课，向四十分钟要质量。历数从教过服装专业课的科目共有8门，其中有《服装结构制图》、《服装工艺》、《服装管理》、《服装CAD》、《服装款式绘画CDR》、《服装模特表演》、《服装中级工考证》、《男衬衫项目》。在此同时于为了能不断的吸收新的知识前往上海参加东华大学服装立裁与制版专业培训；嘉兴市平湖服装协会立裁培训班；纺织协会服装专业骨干教师课改培训等&hellip;&hellip;</span></div>
<p><span style="font-size: small">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 在从教多年来获得的成绩有：&ldquo;曾所备教案获优秀教案奖&rdquo;、&ldquo;所任教的服装自考课程《服装工艺》全国自考通过率100%&rdquo;、&ldquo;所担任的服装CAD与缝制工艺技能团的学生在嘉兴市服装技能比赛中获得一金二银一铜，在浙江省服装技能比赛中获得一金一银，在全国服装技能比赛中取得金奖的好成绩，自己被评为优秀指导教师&rdquo;、&ldquo;获校精教奖&rdquo;、&ldquo;获校级课堂教学一、二等奖&rdquo;、&ldquo;获青年教师基本技能与基础知识竞赛优质奖&rdquo;、&ldquo;获浙江省中职课改专业创新教学设计比赛三等奖&rdquo;、&ldquo;获浙江省中等职业学校专业课教师服装设计职业技能大赛二等奖&rdquo;。</span></p>
<p>&nbsp;</p>', '', '\HrSchoolFiles\1\UpFiles\网站内容\3\36.jpg', '', '', '1', '2', '0', '', '', '', '', '0', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('37', '0', '2', '', '11', '抓住转化的契机', '200310033', '周新妹', '电子电工组', '', '', '', '', '李志平 抓住转化的契机.doc', '201306031223302298128566573900李志平 抓住转化的契机.doc', '15', '2', '0', '', '', '无', '<p>&nbsp;</p>
<div style="text-indent: 34.85pt">苏霍姆林斯基曾感叹：&ldquo;从我手里经过的学生成千上万，奇怪的是，留给我印象最深的并不是无可挑剔的模范生，而是别具特点、与众不同的孩子。&rdquo;教育的这种反差告诉我们，对后进生这样一个&ldquo;与众不同&rdquo;的特殊群体，教育者必须正确认识他们，研究他们，将融融的师爱洒向他们，让这些迟开的&ldquo;花朵&rdquo;沐浴阳光雨露，健康成长。后进生的教育在教育工作中是一个重点，也是一个难点。对他们采取什么样的方法和技巧去做好转化工作，是教育工作者长期探索和研究的课题。</div>', '1', '0', '0')
