INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('45', '0', '2', '', '2', '学生自主，小组合作――校本研训三', '201210001', '王吉', '机电技术部电子电工组', '', '<p><font size="3">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </font><span style="font-size: medium">本学期最后一次校本研训课程由项建峰老师执教，于<span style="font-family: 宋体; mso-spacerun: yes">2013年5月31日</span><span style="font-family: 宋体; mso-spacerun: yes">，在机电部二楼的机房内</span><span style="font-family: 宋体; mso-spacerun: yes">，项老师</span><span style="font-family: 宋体; mso-spacerun: yes">所</span><span style="font-family: 宋体; mso-spacerun: yes">开</span><span style="font-family: 宋体; mso-spacerun: yes">课的课题为：</span><span style="font-family: 宋体; mso-spacerun: yes">《电梯人机界面的设计》。本次课上，首先项老师</span><span style="font-family: 宋体; mso-spacerun: yes">利用</span><span style="font-family: 宋体; mso-spacerun: yes">PPT</span><span style="font-family: 宋体; mso-spacerun: yes">展示</span><span style="font-family: 宋体; mso-spacerun: yes">了电梯的升降</span><span style="font-family: 宋体; mso-spacerun: yes">过程</span><span style="font-family: 宋体; mso-spacerun: yes">，</span><span style="font-family: 宋体; mso-spacerun: yes">吸引了学生的注意力，</span><span style="font-family: 宋体; mso-spacerun: yes">引起了学生的兴趣</span><span style="font-family: 宋体; mso-spacerun: yes">，</span><span style="font-family: 宋体; mso-spacerun: yes">也为课程的开展营造了良好的氛围。随后</span><span style="font-family: 宋体; mso-spacerun: yes">给学生布置任务，任务一：自制电梯人机界面中所用到的元件；任务二：根据电梯人机界面的原理图进行绘制。在学生执行任务前，项老师首先讲了几个绘制过程中可能出现的问题。</span></span><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes"><o:p></o:p></span><span style="font-size: medium"><span style="font-family: 宋体; mso-spacerun: yes">之后的时间为学生自主练</span><span style="font-family: 宋体; mso-spacerun: yes">，练习结束，</span><span style="font-family: 宋体; mso-spacerun: yes">学生提交作业</span><span style="font-family: 宋体; mso-spacerun: yes">，</span><span style="font-family: 宋体; mso-spacerun: yes">项老师随机的抽出几位学生的</span><span style="font-family: 宋体; mso-spacerun: yes">作业</span><span style="font-family: 宋体; mso-spacerun: yes">，进行了点评。</span></span><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes"><o:p></o:p></span></p>
<p><span style="font-size: medium"><span style="font-family: 宋体; mso-spacerun: yes">&nbsp;&nbsp;&nbsp; 看的出来，这堂课项老师准备的非常充分，新颖的课堂引入方法，值得我们学习，本次课上，充分展现了学生自主，小组合作的教学理念，并且也得到了较好的效果，值得我们学习。</span></span></p>
<p><span style="font-size: medium"><span style="font-family: 宋体; mso-spacerun: yes"><img alt="" style="width: 454px; height: 328px" src="/HrSchoolFiles/1/FckEditor/201210001/image/%E9%A1%B9%E5%BB%BA%E5%B3%B0%E5%85%AC%E5%BC%80%E8%AF%BE.jpg" /></span></span></p>
<p>EndFragment--&gt;</p>', '', '\HrSchoolFiles\1\UpFiles\网站内容\2\45.jpg', '', '', '39', '2', '0', '', '', '', '', '0', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('45', '0', '3', '', '4', '丁兰洁简介', '200910002', '服装创新基地委员会', '服装技术部', '', '<p><img width="600" height="450" alt="" src="/HrSchoolFiles/1/FckEditor/200910002/1(4).jpg" /></p>
<p>&nbsp;</p>
<div><span style="font-size: small">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 丁兰洁:1983年10月12日出生在吉林省长春市，2002年8月加入党组织，2006年毕业于吉林工程技术师范学院服装艺术专业,本科学历，文学学位。普通话一级乙等，同年8月就职于浙江省平湖市职业中学，从事服装学科的教学工作。从教7年来担任5年的正副班主任工作，培养的学生进入大专院校深造，在省、市的服装比赛及幼教手工比赛中均有获奖。本人在全国说课比赛、专业技能比赛中获得二等奖，嘉兴市专业教师教学能力水平测试中获奖15次（多次二、三等奖），获得精教奖，被评为局级优秀教师。教科研方面，多篇德育论文在全国、省市中获奖，多篇教学论文在嘉兴市获奖。工作的几年来，任教11门课程，多门自考通过率100%。组织过多次服装毕业设计汇报演出，在工作中始终保持积极的心态和高度的工作热情，视学生的发展为己任，勤学专业技能，创新教学。在上海东华大学学习服装专业知识，获得等级证书。多次下企业学习。宽容，与人为善，坚信腹有诗书气自华。</span></div>
<div><span style="font-size: small">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; 路漫漫其修远兮，吾将上下而求索，我愿做生命的酋长，做一粒不朽的珍珠泪。我渴望朝向艺术之塔，文化之巅，事业之厦，留一行扎实稳健的足迹，迎接新的挑战！</span></div>
<p>&nbsp;</p>', '', '\HrSchoolFiles\1\UpFiles\网站内容\3\45.jpg', '', '', '1', '2', '0', '', '', '', '', '0', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('46', '0', '2', '', '13', '中职生如何通过创新在企业中谋求发展――浙江省首席技师韩其芳来校授课', '201300003', '沈凯超', '机电部电子电工组', '', '<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 24pt; mso-char-indent-count: 2.0"><st1:chsdate w:st="on" isrocdate="False" islunardate="False" day="13" month="4" year="2013"><span class="style31"><span lang="EN-US" style="font-size: 12pt; font-family: 宋体">2013</span></span><span class="style31"><span style="font-size: 12pt; font-family: 宋体">年<span lang="EN-US">4</span>月<span lang="EN-US">13</span>日</span></span></st1:chsdate><span class="style31"><span style="font-size: 12pt; font-family: 宋体">上午三、四两节课，校企协作师傅韩其芳应邀来我校为电气专业二<span lang="EN-US">(11)</span>班学生授课，电子电工组的老师们也一并全程参与，授课主题为<span lang="EN-US">&ldquo;</span>如何创新<span lang="EN-US">&rdquo;</span>。 </span></span><span lang="EN-US" style="font-size: 12pt; font-family: 宋体; color: black"><br />
</span><span class="style31"><span style="font-size: 12pt; font-family: 宋体">　　韩其芳师傅是首届浙江省首席技师，他的交联技能大师工作室获得国家级批准，他本人享受国务院政府特殊津贴。 </span></span><span lang="EN-US" style="font-size: 12pt; font-family: 宋体; color: black"><br />
</span><span class="style31"><span style="font-size: 12pt; font-family: 宋体">　　作为一位大师，他很谦逊，开场白令人印象深刻，<span lang="EN-US">&ldquo;</span>同学们，我只是高中毕业，文凭比你们还低！<span lang="EN-US">&rdquo; </span></span></span><span lang="EN-US" style="font-size: 12pt; font-family: 宋体; color: black"><br />
</span><span class="style31"><span style="font-size: 12pt; font-family: 宋体">　　韩师傅首先通过图片展示及视频播放，向学生们简单介绍了<span lang="EN-US">&ldquo;</span>电线电缆的制作流程与工艺<span lang="EN-US">&rdquo;</span>。复杂的制作工艺、世界先进的生产设备都牢牢吸引了同学们的眼球，丰富了专业知识，拓宽了视眼。其后，韩师傅以一个个创新案例为切入口，满怀激情地讲述了自己的工作经历，他认为他之所以能取得那么多成绩，主要在于<span lang="EN-US">&ldquo;</span>把握机遇、勇于创新<span lang="EN-US">&rdquo;</span>。他告诉学生，想要成为一个好员工并不难，只要听老板的话，踏踏实实认真地做，但想要成为一个优秀的员工，就必须要擅于创新，在一线的工作岗位上不断积累经验、细心观察、敢想敢做，最终在一个技术领域内有所作为。 </span></span><span lang="EN-US" style="font-size: 12pt; font-family: 宋体; color: black"><br />
</span><span class="style31"><span style="font-size: 12pt; font-family: 宋体">　　作为低起点的职中生也好，作为高起点的专职教师也好，一直认为<span lang="EN-US">&ldquo;</span>国家级大师<span lang="EN-US">&rdquo;</span>离我们很遥远，通过此次授课师生一起近距离领略了大师风范，他的淳朴、他的认真、他的谦逊给我们留下了深刻的印象。大师就在我们身边，榜样就在我们身边！（电子电工组<span lang="EN-US">&nbsp;&nbsp;</span>薛花<span lang="EN-US">&nbsp;&nbsp;</span>文）</span></span><span lang="EN-US" style="font-size: 12pt; font-family: 宋体"><o:p></o:p></span></p>', '', '', '', '', '23', '2', '0', '', '', '无', '', '0', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('46', '0', '3', '', '4', '杨春华简介', '200910002', '服装创新基地委员会', '服装技术部', '', '<p><img width="580" height="435" alt="" src="/HrSchoolFiles/1/FckEditor/200910002/杨春华3.jpg" /></p>
<p>&nbsp;</p>
<div style="line-height: 20pt; text-indent: 24pt">
<div style="line-height: 20pt"><span style="font-size: 12pt">
<div>杨春华，<span>1997年毕业于江西科技师范学院服装设计专业,获文学学士学位。同年，任教于平湖职业中专至今。</span></div>
<div>作为教研组长，积极组织和参与服装专业的课改、教改工作，参加示范校建设，负责《小型服装企业的创建和管理手册》的课题研究和校本教材的编写；先后与<span>8位教师结对辅导，部分教师已成为教师骨干。</span></div>
<div>作为专业课教师，本人担任过服装设计、服装制板和服装工艺等所有服装专业课的教学。本着关爱每一位学生，不放弃就有希望的教学理念，在师生的共同努力下，取得了可喜的成绩。曾有<span>6位同学高职考试总分名列浙江省前三名，上线率一直高达100%；《构成》自考的通过率100%；辅导的7名美术类考生也全部考入艺术类大学深造。班主任工作细致、到位，所带班级曾获校文明班级、嘉兴市优秀团支部等荣誉，先后有四位学生高中毕业前就光荣入党。</span></div>
<div>注重教科研学习，积极参加全国骨干教师等培训。多个课件获嘉兴市一等奖；十多篇论文地市级获奖或发表；主持的课题获嘉兴市二等奖；指导学生参加服装设计比赛获&ldquo;最佳创意奖&rdquo;和&ldquo;最佳配件奖&rdquo;等。被派到服装企业合作研发了十多个系列男装；设计的童装商标被录用，被鑫凯莱服装公司聘请为服装设计师。带领学生进行了&ldquo;扬格斯特&rdquo;床单、被套等的研发，得到同仁的肯定。自己幸运地评为市优秀班主任<span>、名师和第八、九批嘉兴市学科带头人等。</span></div>
<div><span>&nbsp;&nbsp; </span>我将继续努力学习和工作，为本校服装专业办出名优特色尽自己的微薄之力。</div>
</span></div>
</div>
<p>&nbsp;</p>', '', '\HrSchoolFiles\1\UpFiles\网站内容\3\46.jpg', '', '', '3', '2', '0', '', '', '', '', '0', '0', '0')
