INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('52', '0', '2', '', '13', '校企合作――英厚机械公司参观学习', '201300003', '沈凯超', '机电技术部电子电工组', '', '<p class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt; text-indent: 21pt"><span style="font-size: 12pt; mso-hansi-font-family: 宋体"><font face="宋体"><img style="height: 372px; width: 522px" alt="" src="/HrSchoolFiles/1/FckEditor/201300003/0GIMGP5PXQKGAQ5H(6`3@BP.jpg" /></font></span></p>
<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 21pt">&nbsp;</p>
<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 21pt"><span style="font-size: 12pt; mso-hansi-font-family: 宋体"><font face="宋体">为了更好地了解企业的动态发展方向，促进校企合作的顺利进行，<span lang="EN-US">5</span>月<span lang="EN-US">31</span>号上午，李晶老师、陆元杰老师、沈翠婷老师走访了陶师傅所在的英厚机械公司。<span lang="EN-US"><o:p></o:p></span></font></span></p>
<p class="MsoNormal" style="margin: 0cm 0cm 0pt"><font face="宋体"><span lang="EN-US" style="font-size: 12pt; mso-hansi-font-family: 宋体"><span style="mso-tab-count: 1">&nbsp;&nbsp;&nbsp; </span></span><span style="font-size: 12pt; mso-hansi-font-family: 宋体">陶师傅带领我们参观了工厂的机械设备，让我们耳目一新，并向我们详细地介绍了加工书本的流程以及各个机器的工作原理，这些设备多数采用先进技术，渗入<span lang="EN-US">PLC</span>、显示屏技术，印象最深的是工厂内整齐的电气线路安装，工厂里的电气线路安装与我们平时教给学生的线路安装差距较大，要求也更高，所以，在我们今后的教学过程中，可以渗透工厂的电气线路安装的要求，这样有利于学生技术水平的提高。最后，和师傅一起讨论了如何进行校企合作授课。<span lang="EN-US"><o:p></o:p></span></span></font></p>
<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 21pt"><span style="font-size: 12pt; mso-hansi-font-family: 宋体"><font face="宋体">本次走访中，我们也发现了自己理论知识与实践操作的差距，需要进一步地加强校企合作，取其精华，传授给学生。<span lang="EN-US"><o:p></o:p></span></font></span></p>
<p>&nbsp;</p>', '', '', '', '', '32', '2', '0', '', '', '', '', '0', '0', '0')
