INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('54', '0', '2', '', '15', '王冬梅简介', '201300003', '沈凯超', '机电部电子电工组', '', '<p style="text-align: center"><img alt="" width="564" height="384" src="/HrSchoolFiles/1/FckEditor/201300003/王冬梅.jpg" /></p>
<p style="text-align: center">&nbsp;</p>
<p style="margin: 0cm 0cm 0pt"><font size="3"><font face="宋体"><b style="mso-bidi-font-weight: normal">王冬梅<span lang="EN-US">: </span></b>女，<span lang="EN-US">1992</span>年<span lang="EN-US">7</span>月毕业于杭州电子工业学院；<span lang="EN-US">1992</span>年<span lang="EN-US">8</span>月至今，平湖职业中专任教电子电工专业课。电子电工专业高级教师。<span lang="EN-US">2002</span>年取得维修电工高级工证书，<span lang="EN-US">2005</span>年取得维修电工技师证书。<span lang="EN-US" style="color: black; mso-bidi-font-family: Arial"><o:p></o:p></span></font></font></p>
<p class="MsoNormal" style="margin: 0cm 0cm 0pt"><span lang="EN-US" style="font-size: 12pt; font-family: 宋体"><span style="mso-spacerun: yes">&nbsp;&nbsp;&nbsp; </span></span><span style="font-size: 12pt; font-family: 宋体">任职以来获得过平湖市&ldquo;优秀班主任&rdquo;的称号，平湖市第八批名师和平湖市第十一批名师。<span lang="EN-US"><o:p></o:p></span></span></p>
<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 24pt; mso-pagination: widow-orphan; mso-char-indent-count: 2.0"><span style="font-size: 12pt; font-family: 宋体">一篇案例获全国一等奖，一篇论文获省二等奖；一篇心理健康案例获省三等奖；一篇论文获嘉兴市一等奖，四篇论文和一篇调研报告获嘉兴市二等奖。</span><span style="font-size: 12pt; font-family: 宋体; color: black; mso-bidi-font-family: 宋体; mso-font-kerning: 0pt">三轮平湖市级课题《电子电工专业理论、技能与实训一体化实践与探索》获二等奖。<span lang="EN-US"><o:p></o:p></span></span></p>
<p style="margin: 0cm 0cm 0pt"><font size="3"><font face="宋体"><span lang="EN-US" style="color: black; mso-bidi-font-family: Arial">&nbsp; <span style="mso-spacerun: yes">&nbsp; </span></span><span style="color: black; mso-bidi-font-family: Arial">教育教学感言：用欣赏的眼光看学生，用宽容的心态对学生。</span></font></font></p>', '', '\HrSchoolFiles\1\UpFiles\网站内容\2\54.jpg', '', '', '26', '2', '0', '', '', '无', '', '0', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('55', '0', '2', '', '15', '谢华林简介', '201300003', '沈凯超', '机电部电子电工组', '', '<p class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt"><b style="mso-bidi-font-weight: normal"><span style="font-size: 12pt; font-family: 宋体; mso-bidi-font-family: 宋体; mso-font-kerning: 0pt"><img alt="" width="451" height="551" src="/HrSchoolFiles/1/FckEditor/201300003/谢华林.jpg" /></span></b></p>
<p class="MsoNormal" style="text-align: center; margin: 0cm 0cm 0pt">&nbsp;</p>
<p class="MsoNormal" style="margin: 0cm 0cm 0pt">&nbsp;</p>
<p class="MsoNormal" style="margin: 0cm 0cm 0pt"><b style="mso-bidi-font-weight: normal"><span style="font-size: 12pt; font-family: 宋体; mso-bidi-font-family: 宋体; mso-font-kerning: 0pt">谢华林：</span></b><span style="font-size: 12pt; font-family: 宋体; mso-bidi-font-family: 宋体; mso-font-kerning: 0pt">男，中共党员，<span lang="EN-US">1987</span>年<span lang="EN-US">7</span>月毕业于浙江农业大学宁波分校机电专业专科毕业，</span><span lang="EN-US" style="font-size: 12pt; font-family: 宋体; mso-font-kerning: 0pt">2003</span><span style="font-size: 12pt; font-family: 宋体; mso-font-kerning: 0pt">年<span lang="EN-US">6</span>月浙江大学电气工程及自动化函授</span><span style="font-size: 12pt; font-family: 宋体; mso-bidi-font-family: 宋体; mso-font-kerning: 0pt">本科；中学电子电工高级教师，</span><span style="font-size: 12pt; font-family: 宋体; mso-font-kerning: 0pt">嘉兴市第九、十批学科带头人</span><span style="font-size: 12pt; font-family: 宋体; mso-bidi-font-family: 宋体; mso-font-kerning: 0pt">。<span lang="EN-US">1987</span>年参加工作，历任班主任<span lang="EN-US">5</span>年、兼生活指导老师<span lang="EN-US">5</span>年、校团委书记<span lang="EN-US">11</span>年，</span><span style="font-size: 12pt; font-family: 宋体">政教处工作<span lang="EN-US">2001</span>年至今，</span><span style="font-size: 12pt; font-family: 宋体; mso-bidi-font-family: 宋体; mso-font-kerning: 0pt">现任平湖市职业中专电子电工专业教师、校教导处兼政教处主任。</span><span style="font-size: 12pt; font-family: 宋体; mso-font-kerning: 0pt">曾荣获平湖市创卫先进个人、德育先进个人、优秀教师、局先进党员，嘉兴市优秀团干部、德育先进个人、教育百杰。<span lang="EN-US"><o:p></o:p></span></span></p>
<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 24pt; mso-pagination: widow-orphan; mso-char-indent-count: 2.0"><span style="font-size: 12pt; font-family: 宋体">教学工作中能结合学生实际，课程特点，因材施教，坚持&ldquo;就业为导向、能力为本位、服务为宗旨&rdquo;的职教理念，理实一体化教学，以项目教学引领，着力学生动手能力提高，挖掘中职学生学习潜力，学有收获。<span lang="EN-US"><o:p></o:p></span></span></p>
<p class="MsoNormal" style="margin: 0cm 0cm 0pt; mso-pagination: widow-orphan"><span style="font-size: 12pt; font-family: 宋体">德育工作<span lang="EN-US"><o:p></o:p></span></span></p>
<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 24pt; mso-pagination: widow-orphan; mso-char-indent-count: 2.0"><span style="font-size: 12pt; font-family: 宋体">在<span lang="EN-US">25</span>年的教育教学实践中，作为一名基层中共党员及人民教师，始终牢记党的宗旨全心全意为人民服务，在自己的教育教学工作岗位上，尽责任、用智慧，教书育人、为人师表、关心学生、团结同志、乐以助人、为人诚实、遵纪守法、有团结协作的团队精神。在教育改革中，以学生为本，落实素质教育；不断提高自身的素质，把教学作为一门艺术，把教育作为一项系统工程，对教育事业热诚，教育教学水平稳步提高。一分耕耘，一分收获，以自己教育效果、教学成绩和良好的品德、人格，赢得了学生和家长的尊重。<span lang="EN-US"><o:p></o:p></span></span></p>
<p class="MsoBodyText2" align="left" style="text-align: left; margin: 0cm 0cm 0pt; line-height: 16pt; text-indent: 24pt; mso-char-indent-count: 2.0; mso-line-height-rule: exactly"><span style="font-size: 12pt; font-family: 宋体; mso-bidi-font-weight: bold">协助分管校长工作，履行好政教处主任工作职责。<span lang="EN-US"><o:p></o:p></span></span></p>
<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 24pt; mso-pagination: widow-orphan; mso-char-indent-count: 2.0"><span style="font-size: 12pt; font-family: 宋体">多年来所撰写的论文获全国职业教育优秀论文评选三等奖<span lang="EN-US">1</span>篇；</span><span style="font-size: 12pt; font-family: 宋体; mso-font-kerning: 0pt">嘉兴市职教论文评选获一等奖<span lang="EN-US">1</span>篇；二等奖<span lang="EN-US">5</span>篇；</span><span style="font-size: 12pt; font-family: 宋体">三等奖<span lang="EN-US">3</span>篇。<span lang="EN-US"><o:p></o:p></span></span></p>
<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 24pt; mso-pagination: widow-orphan; mso-char-indent-count: 2.0"><span style="font-size: 12pt; font-family: 宋体; mso-ansi-language: EN-GB">主持</span><span style="font-size: 12pt; font-family: 宋体; color: black; mso-bidi-font-family: 宋体; mso-font-kerning: 0pt">《中等职业学校实施值周班工作制度的实践》课题嘉兴市立项并结题<span lang="EN-US">2006</span>年<span lang="EN-US">11</span>月；</span><span style="font-size: 12pt; font-family: 宋体; mso-ansi-language: EN-GB">主持</span><span style="font-size: 12pt; font-family: 宋体; color: black; mso-bidi-font-family: 宋体; mso-font-kerning: 0pt">《建设民主、阳光的班级集体DD职业中学班级集体建设的实践和探索》课题平湖市立项并结题<span lang="EN-US">2011</span>年<span lang="EN-US">1</span>月；</span><span style="font-size: 12pt; font-family: 宋体; mso-ansi-language: EN-GB">主持校本教材《<span lang="EN-GB">PLC</span>控制技术校本教材开发</span><span style="font-size: 12pt; font-family: 宋体">》，将于<span lang="EN-US">2013</span>年<span lang="EN-US">6</span>月由清华出版社出版；</span><span style="font-size: 12pt; font-family: 宋体; mso-ansi-language: EN-GB">主持</span><span style="font-size: 12pt; font-family: 宋体; color: black; mso-bidi-font-family: 宋体; mso-font-kerning: 0pt">《</span><span lang="EN-US" style="font-size: 12pt; font-family: 宋体">PLC</span><span style="font-size: 12pt; font-family: 宋体">控制技术与技能训练校本课程的创新研究》课题<span lang="EN-US">2012</span>年<span lang="EN-US">9</span>月</span><span style="font-size: 12pt; font-family: 宋体; color: black; mso-bidi-font-family: 宋体; mso-font-kerning: 0pt">嘉兴市立项课题。</span><span style="font-size: 12pt; font-family: 宋体">还先后主编了平湖职中《团员手册》、《志愿者手册》、《学生手册》、《班主任工作手册》。</span><span lang="EN-US" style="font-size: 12pt; font-family: 宋体; color: black; mso-bidi-font-family: 宋体; mso-font-kerning: 0pt"><o:p></o:p></span></p>
