INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('69', '0', '2', '', '30', '单片机装置安装与调试', '200310018', '任广红', '机电技术部 电子技术组', '', '<div style="text-indent: 21pt; line-height: 150%"><span style="font-size: 12pt; line-height: 150%">单片机控制技术是一种新兴的微控制技术，使用非常广泛，主要应用到工控技术、日常家电、仪器仪表、机电控制、航空航天、军事设备等各个领域，单片机控制技术已经成为电子系统智能化的最好工具。</span></div>
<div style="text-indent: 21pt; line-height: 150%"><span style="font-size: 12pt; line-height: 150%">本项目采用中国亚龙集团提供的</span><span style="font-size: 12pt; line-height: 150%">YL-236</span><span style="font-size: 12pt; line-height: 150%">设备和</span><span style="font-size: 12pt; line-height: 150%">YL-G001</span><span style="font-size: 12pt; line-height: 150%">智能搬运装置。</span><span style="font-size: 12pt; line-height: 150%">其采用模块化，软硬结合，模块之间联接灵活，加深对单片机原理理解,能够完成I/O实训，A/D实训，D/A实训，LED\LCD点阵模块实验，电机控制等，体现了灵活性、开放、创新、综合、跨领域、跨专业的设计理念。</span></div>
<div style="text-indent: 21pt; line-height: 150%"><span style="font-size: 12pt; line-height: 150%">本项目的具体任务是按照工作任务书要求，选择模块、连接电路、组成单片机控制装置。根据装置的工作说明和控制要求，编写控制程序完成整体调试和填写调试记录。</span></div>', '', '', '', '', '17', '2', '0', '', '', '', '', '0', '0', '0')
