INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('96', '0', '2', '', '40', '课程介绍', '200900001', '顾佳春', '机电技术部 电子电工组', '', '<p class="p0" style="margin-top: 0pt; margin-bottom: 0pt; text-indent: 23.625pt; line-height: 150%"><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">本书是我校为创建国家级示范性职业中专，进行重点专业课程改革的校本教材之一。按照国家职业标准，根据浙江省职业教育课程改革的精神和要求，结合地区对机电专业人才能力的要求，遵</span><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">循学生的认知规律，打破传统的学科课程体系，</span><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">总结多年教学经验编写而成的全新教材。</span><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''"><o:p></o:p></span></p>
<p class="p0" style="margin-top: 0pt; margin-bottom: 0pt; text-indent: 21pt; line-height: 150%"><span style="font-weight: bold; font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">本书的内容：</span><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">&nbsp;CA6140车床电气控制线路的安装与电动葫芦电气控制线路安装两个项目。</span><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''"><o:p></o:p></span></p>
<p class="p0" style="margin-top: 0pt; margin-bottom: 0pt; text-indent: 21pt; line-height: 150%"><span style="font-weight: bold; font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">本书的特色：&nbsp;</span><span style="font-weight: bold; font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''"><o:p></o:p></span></p>
<p class="p0" style="margin-top: 0pt; margin-bottom: 0pt; margin-left: 42pt; text-indent: -18pt; line-height: 150%; text-align: left"><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">1、&nbsp;</span><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">以项目引领，以任务驱动，</span><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">以学生的行为为导向，采取</span><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">任务</span><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">的形式对机床电气控制</span><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">线路</span><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">进行重新建构。</span><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">每个项目按&ldquo;学习目标&rdquo;、&ldquo;项目情境&rdquo;、&ldquo;任务要求&rdquo;、&ldquo;任务分析&rdquo;、&ldquo;知识准备&rdquo;&ldquo;任务实施&rdquo;、&ldquo;任务评价&rdquo;、&ldquo;知识拓展&rdquo;、&ldquo;知识巩固&rdquo;编写，从提出问题到解决问题，循序渐进，巩固提高。</span><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''"><o:p></o:p></span></p>
<p class="p0" style="margin-top: 0pt; margin-bottom: 0pt; margin-left: 42pt; text-indent: -18pt; line-height: 150%; text-align: left"><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">2、&nbsp;</span><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">项目代表性强。两个项目充分体现了不同的电气控制特点，实训中由易到难，由浅入深，层层递进。</span><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''"><o:p></o:p></span></p>
<p class="p0" style="margin-top: 0pt; margin-bottom: 0pt; margin-left: 42pt; text-indent: -18pt; line-height: 150%; text-align: left"><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">3、&nbsp;</span><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">教材图文并茂，</span><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">形象、直观。教材中</span><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">尽可能使用图片展示各个知识点</span><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''">，从元器件、电气线路图、电气安装接线图到机床外观图及结构图都清晰、明了，给读者以形象、直观的认知，便于理解、安装接线。</span><span style="font-size: 10.5pt; color: rgb(0,0,0); font-family: ''宋体''; mso-spacerun: ''yes''"><o:p></o:p></span></p>
