INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('112', '0', '2', '', '30', '机电一体化设备组装与调试', '200310018', '任广红', '机电技术部 电子电工组', '', '<div style="line-height: 150%"><b><span style="font-size: 12pt; line-height: 150%">一、实训设备</span></b><span style="font-size: 12pt; line-height: 150%">：亚龙</span><span style="font-size: 12pt; line-height: 150%">YL&mdash;235A</span></div>
<div style="line-height: 150%"><b><span style="font-size: 12pt; line-height: 150%">二、概述</span></b></div>
<div style="text-indent: 11.8pt; line-height: 150%"><b><span style="font-size: 12pt; line-height: 150%">1.</span></b><b><span style="font-size: 12pt; line-height: 150%">实训台的机械结构</span></b></div>
<div style="line-height: 150%"><span style="font-size: 12pt; line-height: 150%">&nbsp;&nbsp;&nbsp;&nbsp; </span><span style="font-size: 12pt; line-height: 150%">铝合金导轨式实训台、上料机构、上料检测机构、搬运机构、物料传送和分拣机构等组成。各个机构紧密相连，学生可以自由组装和调试。</span></div>
<div style="text-indent: 11.8pt; line-height: 150%"><b><span style="font-size: 12pt; line-height: 150%">2.</span></b><b><span style="font-size: 12pt; line-height: 150%">实训台的电气控制组成</span></b></div>
<div style="line-height: 150%"><span style="font-size: 12pt; line-height: 150%">&nbsp;&nbsp;&nbsp; </span><span style="font-size: 12pt; line-height: 150%">控制系统采用模块组合式，由触摸屏模块、</span><span style="font-size: 12pt; line-height: 150%">PLC</span><span style="font-size: 12pt; line-height: 150%">模块、变频器模块、按钮模块、电源模块、接线端子排和各种传感器等组成。触摸屏模块、</span><span style="font-size: 12pt; line-height: 150%">PLC</span><span style="font-size: 12pt; line-height: 150%">模块、变频器模块、按钮模块等可按实训需要进行组合、安装、调试。</span></div>
<div style="text-indent: 17.7pt; line-height: 150%"><b><span style="font-size: 12pt; line-height: 150%">3.</span></b><b><span style="font-size: 12pt; line-height: 150%">所包含技术学科</span></b></div>
<div style="line-height: 150%"><span style="font-size: 12pt; line-height: 150%">&nbsp;&nbsp;&nbsp;&nbsp; </span><span style="font-size: 12pt; line-height: 150%">该系统包含了机电一体化专业学习中所涉及的诸如电机驱动、机械传动、气动、触摸屏控制、可编程控制器、传感器，变频调速等多项技术，为学生提供了一个典型的综合实训环境，使学生对过去学过的诸多单科的专业和基础知识，在这里能得到全面的认识、综合的训练和实际运用。</span></div>
<div style="margin: 0cm 0cm 0pt 21pt; line-height: 150%"><b><span style="font-size: 12pt; line-height: 150%">4. </span></b><b><span style="font-size: 12pt; line-height: 150%">实训项目</span></b><b><span style="font-size: 12pt; line-height: 150%"><br />
&nbsp;&nbsp;&nbsp; </span></b><span style="font-size: 12pt; line-height: 150%">1</span><span style="font-size: 12pt; line-height: 150%">）自动检测技术使用实训</span><span style="font-size: 12pt; line-height: 150%"><br />
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2</span><span style="font-size: 12pt; line-height: 150%">）气动技术应用实训</span><span style="font-size: 12pt; line-height: 150%"><br />
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3</span><span style="font-size: 12pt; line-height: 150%">）可编程控制器编程实训</span><span style="font-size: 12pt; line-height: 150%"><br />
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 4</span><span style="font-size: 12pt; line-height: 150%">）</span><span style="font-size: 12pt; line-height: 150%">电气控制电路实训</span><span style="font-size: 12pt; line-height: 150%"><br />
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5</span><span style="font-size: 12pt; line-height: 150%">）</span><span style="font-size: 12pt; line-height: 150%">变频器应用实训</span><span style="font-size: 12pt; line-height: 150%"><br />
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 6</span><span style="font-size: 12pt; line-height: 150%">）触摸屏应用实训</span><span style="font-size: 12pt; line-height: 150%"><br />
&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 7</span><span style="font-size: 12pt; line-height: 150%">）自动控制技术教学与实训</span><span style="font-size: 12pt; line-height: 150%"><br />
