INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('118', '0', '2', '', '2', '相互学习，共同进步――绍兴职教教师来我校机电部考察学习', '201210001', '王吉', '机电部电子电工组', '', '<div class="Section0" style="layout-grid:  15.6pt none">
<p class="p0" style="margin-top: 0pt; margin-bottom: 0pt"><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes">&nbsp;&nbsp;&nbsp; 为促进兄弟学校间更好的发展，绍兴县各个职业技术学校的二十几位老师于<font face="Times New Roman">5</font><font face="宋体">月</font><font face="Times New Roman">16</font><font face="宋体">日来我校机电部进行了考察学习。</font></span><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes"><o:p></o:p></span></p>
<p class="p0" style="margin-top: 0pt; text-indent: 21pt; margin-bottom: 0pt"><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes">考察期间，老师们听取了机电部盛丽君老师与周新妹老师的课。</span><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes"><o:p></o:p></span></p>
<p class="p0" style="margin-top: 0pt; text-indent: 21pt; margin-bottom: 0pt"><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes">盛老师所设计的课程为《</span><span style="font-family: 宋体; font-size: 10.5pt; font-weight: normal; mso-spacerun: yes">三相异步电动机的正反转控制线路&mdash;&mdash;接触器联锁</span><span style="font-family: 宋体; font-size: 10.5pt; font-weight: normal; mso-spacerun: yes">》，课上盛老师从实际生产中的起重机的吊钩需要上升与下降；机床工作台需要前进与后退；万能铣床的主轴需要正转与反转，这样的较形象的现象来引入本次的课程。首先向同学们展示本次课的学习任务，课程的中心环节是以动画的形式向同学们展示电动机是如何实现正反转控制的。课上教师与学生配合默契，课堂气氛相当的融洽。</span><span style="font-family: 宋体; font-size: 10.5pt; font-weight: normal; mso-spacerun: yes"><o:p></o:p></span></p>
<p class="p0" style="margin-top: 0pt; text-indent: 21pt; margin-bottom: 0pt"><span style="font-family: 宋体; font-size: 10.5pt; font-weight: normal; mso-spacerun: yes">周老师所设计的课程为《</span><span style="font-family: 宋体; font-size: 10.5pt; font-weight: normal; mso-spacerun: yes">电动扶梯PLC程序设计</span><span style="font-family: 宋体; font-size: 10.5pt; font-weight: normal; mso-spacerun: yes">》，课上周老师从实际生活中电动扶梯这样一个与生活息息相关的实际现象引入，</span><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes">通过仿真软件操作线路</span><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes">演示，</span><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes">现场手机拍照展示图片</span><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes">讲解课程，随后，由学生分小组合作完成本次课的任务，在课程的最后环节，由学生来展示他们的设计成果。课程设计环节环环相扣，效果明显。</span><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes"><o:p></o:p></span></p>
<p class="p0" style="margin-top: 0pt; text-indent: 21pt; margin-bottom: 0pt"><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes">课后，利用中午时间，在机电部会议室进行了评课。首先老师们对我们机电部的学生素质素养进行了肯定，反应学生上课课堂纪律较好；其次对我们老师的教育教学能力进行了肯定，并向周老师讨教了课上一些先进的教学软件的用法；再次对机电部实训场所现场干净整齐的环境进行了赞扬。</span><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes"><o:p></o:p></span></p>
<p class="p0" style="margin-top: 0pt; text-indent: 21pt; margin-bottom: 0pt"><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes">本次考察交流，相信绍兴职业技术学校的老师们收获颇多，当然对于我们机电部也是一次较好的展示自我风采的机会，真正达到了双赢的效果。（机电部电子电工组 王吉）</span><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes"><o:p></o:p></span></p>
<p class="p0" style="margin-top: 0pt; text-indent: 21pt; margin-bottom: 0pt"><span style="font-family: 宋体; font-size: 10.5pt; mso-spacerun: yes"><o:p></o:p></span></p>
<p class="p0" style="text-align: left; line-height: 150%; margin-top: 0pt; margin-bottom: 0pt"><span style="font-family: 宋体; font-size: 10.5pt; font-weight: normal; mso-spacerun: yes"><o:p></o:p></span></p>
<p class="p0" style="margin-top: 0pt; text-indent: 21pt; margin-bottom: 0pt"><span style="font-family: 宋体; font-size: 10.5pt; font-weight: normal; mso-spacerun: yes"><o:p></o:p></span></p>
<p class="p0" style="margin-top: 0pt; margin-bottom: 0pt">&nbsp;</p>
</div>
