INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('121', '0', '2', '', '10', '中职高一新生班级管理工作的难点及解决策略分析', '200310033', '周新妹', '机电技术部电子技术组', '', '', '', '', '中职高一新生班级管理工作的难点及解决策略分析.doc', '201306191255324756279370427000中职高一新生班级管理工作的难点及解决策略分析.doc', '34', '2', '0', '', '', '', '<div style="line-height: 25pt"><b><span style="color: black">[</span></b><b><span style="color: black">摘&nbsp;要]：</span></b><span style="color: black">班级管理工作关系到整个学校的稳定，是整个学校德育工作的重要组成部分，而班主任是班级管理的第一抓手。面对学习兴趣不浓、行为习惯较差、自控能力不强的中职高一新生，班主任如何克服事务杂多的客观事实，冷静、积极思考教育智慧，采取有效的管理措施，管理好班级、教育好学生，做一个优秀的中职班主任呢？是一个值得长期探索与实践的问题。本文结合笔者多年的班主任工作实践经验，阐述了中职高一新生班级管理工作的难点及解决策略，并在实践中收到了较好的效果。</span></div>', '1', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('122', '0', '2', '', '10', '中职学校电工专业教学改革的实践与探索', '200310033', '周新妹', '机电技术部电子技术组', '', '', '', '', '中职学校电工专业教学改革的实践与探索.doc', '201306190922400381418718020000中职学校电工专业教学改革的实践与探索.doc', '38', '2', '0', '', '', '', '<div style="text-indent: 21.1pt"><b><span style="font-size: 10.5pt">摘要：</span></b><span style="font-size: 10.5pt">电工专业课作为中职学校机电类专业教学工作的重要组成部分，在电工考证、高职考试、培养学生职业素养方面起着重要的作用。结合多年电工专业课程的教学实践，在对学校原电工专业课教学分析的基础上，通过加强教师培训力度，编写校本教材，改变教学方法，实施多元化评价，这四个方面的实践来不断提高电工专业课的教学质量，取得了较好成效。</span></div>', '1', '0', '0')
INSERT INTO [EDU_WZXT_MHXT_WZWZ]([ID] ,[SCHOOLID] ,[WEBID] ,[TYPENAME] ,[LMID] ,[TITLE] ,[PUBLISHERNAME] ,[AUTHOR] ,[AUTHORDEPART] ,[PUBLISHDATE] ,[CONTENT] ,[URL] ,[IMAGEURL] ,[ATTACHMENTNAME] ,[ATTACHMENT] ,[CHICKNUB] ,[AUDITSTATU] ,[AUDITOR] ,[AUDITORNAME] ,[AUDITTIME] ,[REMARK] ,[SUMMARY] ,[DISPLAYTYPE] ,[OPENFLAG] ,[LLQX]) VALUES('123', '0', '2', '', '30', '电子产品装接与调试', '200310018', '任广红', '机电技术部 电子技术组', '', '<div style="line-height: 150%"><b><span style="font-size: 12pt; color: black; line-height: 150%">一、概述</span></b><span style="color: black; line-height: 150%">：</span></div>
