INSERT INTO [EDU_GB_GMJJHYFL]([DM] ,[MC] ,[SM]) VALUES('204', '竹、藤、棕、草等制品制造', '指除木材以外，以竹、藤、棕、草等天然植物为原料生产制品的活动，但不包括家具的制造')
INSERT INTO [EDU_GB_GMJJHYFL]([DM] ,[MC] ,[SM]) VALUES('2041', '竹制品制造', '指竹胶合板、竹地板、竹丝板等竹制品的制造')
